import React, { useEffect } from "react";
import { useHistory } from "react-router-dom"; // Ensure correct import

const Login = () => {
  const history = useHistory(); // useHistory hook for navigation

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        history.push("/profile"); // Redirect to profile if user is already logged in
      }
    });

    return () => unsubscribe();
  }, [history]);

  const googleLogin = () => {
    const provider = new GoogleAuthProvider();
    signInWithPopup(auth, provider)
      .then((result) => {
        console.log(result);
        if (result.user) {
          toast.success("User Logged In Successfully", {
            position: "top-center",
          });
          history.push("/profile"); // Redirect after successful login
        }
      })
      .catch((error) => {
        console.error("Google Sign-in Error:", error);
        toast.error("Failed to sign in with Google", {
          position: "top-center",
        });
      });
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-xs">
        <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
          {/* Your form fields */}
          <div className="flex items-center justify-center">
            <button
              onClick={googleLogin}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              type="button"
            >
              Log In with Google
            </button>
          </div>
        </form>
        <p className="text-center text-gray-500 text-xs">
          &copy;2020 Acme Corp. All rights reserved.
        </p>
      </div>
    </div>
  );
};

export default Login;
