import React, { useState } from "react";

const Search = () => {
  const [borderColor, setBorderColor] = useState("border-gray-400");

  const handleFocus = () => {
    setBorderColor("border-red-700");
  };

  const handleBlur = () => {
    setBorderColor("border-gray-400");
  };

  return (
    <div className="flex justify-center mt-8">
      <div
        className={`flex items-center bg-white rounded-full border ${borderColor} p-1`}
      >
        <input
          style={{ width: "28rem" }}
          placeholder="YouTube link here..."
          className="pl-5 h-10 text-sm text-gray-900 rounded-full focus:ring-blue-500 focus:border-blue-500 outline-none placeholder:text"
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
        <button
          type="submit"
          className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-full text-base px-4 h-10"
        >
          Search
        </button>
      </div>
    </div>
  );
};

export default Search;
