import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PieChart,
  Pie,
  Tooltip,
  Cell,
  ResponsiveContainer,
  Legend,
} from "recharts";

const Senti = () => {
  const defaultUrl = "https://www.youtube.com/watch?v=c1GMcr2x4dI"; // Default URL
  const [videoUrl, setVideoUrl] = useState(""); // Initially empty input
  const [commentData, setCommentData] = useState(null);
  const [pieChartData, setPieChartData] = useState([]);

  // Fetch default data on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post("http://127.0.0.1:5000/analyze", {
          url: defaultUrl,
        });

        // Transform the data for the Pie Chart
        const dataForPieChart = [
          { name: "Positive Comments", value: response.data.positive_comments },
          { name: "Negative Comments", value: response.data.negative_comments },
        ];

        setPieChartData(dataForPieChart);
        console.log(response.data);
        setCommentData(response.data);
      } catch (error) {
        console.error("Error fetching default data:", error);
      }
    };

    fetchData(); // Fetch data for the default URL
  }, []); // Empty dependency array to run only once on mount

  // Fetch data for entered URL when Submit is clicked
  const handleSubmit = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/analyze", {
        url: videoUrl,
      });

      // Transform the data for the Pie Chart
      const dataForPieChart = [
        { name: "Positive Comments", value: response.data.positive_comments },
        { name: "Negative Comments", value: response.data.negative_comments },
      ];
      setPieChartData(dataForPieChart);
      setCommentData(response.data);
    } catch (error) {
      console.error("Error sending URL to backend:", error);
    }
  };

  const COLORS = ["#0088FE", "#FFBB28"]; // Set colors for the segments

  return (
    <div className="flex-col justify-center items-center mt-10">
      <div>
        <div className="mb-5">
          <input
            type="text"
            placeholder="Enter Youtube Link"
            className="p-3 ml-3 mr-2 bg-gray-100 text-black rounded-full outline-none"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
          />
          <button
            className="bg-blue-700 p-3 text-white rounded-full"
            onClick={handleSubmit}
          >
            Submit
          </button>
        </div>
      </div>
      <div>
        <div>
          {commentData && (
            <div className="bg-white p-5 rounded shadow-md text-center">
              <h2 className="text-2xl font-bold mb-3">Video Analysis</h2>
              <div className="w-25 h-25 roundd">
                <img src={commentData.profile_picture_url} alt="" />
              </div>
              <p className="text-lg">
                Total Comments: {commentData.total_comments}
              </p>
              <p className="text-lg">
                Positive Comments: {commentData.positive_comments}
              </p>
              <p className="text-lg">
                Negative Comments: {commentData.negative_comments}
              </p>
              <p className="text-lg">Channel Name: {commentData.channel}</p>
              <p className="text-lg">Total Likes: {commentData.total_likes}</p>
            </div>
          )}
        </div>
        <div>
          {pieChartData.length > 0 && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie
                  data={pieChartData}
                  cx="50%"
                  cy="50%"
                  outerRadius={150}
                  fill="#8884d8"
                  dataKey="value"
                  label
                >
                  {pieChartData.map((entry, index) => (
                    <Cell
                      key={`cell-${index}`}
                      fill={COLORS[index % COLORS.length]}
                    />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>
      </div>
    </div>
  );
};

export default Senti;
