import React, { useEffect } from "react";
import { auth } from "./firebase";
import { toast } from "react-toastify";

const Profile = () => {
  useEffect(() => {
    const checkAuthState = async () => {
      try {
        const user = auth.currentUser;
        if (!user) {
          // If user is not logged in, redirect to login page
          window.location.href = "/profile";
        }
      } catch (error) {
        console.log(error.message);
      }
    };

    checkAuthState();
  }, []);

  async function handleLogout() {
    try {
      await auth.signOut();
      // After logout, redirect to the homepage or a restricted access page
      window.location.href = "/";
      toast.success("User Logged Out Successfully", {
        position: "top-center",
        autoClose: 2000,
      });
    } catch (error) {
      console.log(error.message);
    }
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <header className="px-2 py-4 mt-16 flex flex-col justify-center items-center text-center">
        {/* Your profile content here */}
        <div className="p-10">
          <button
            onClick={handleLogout}
            className="bg-blue-700 text-white p-2 rounded-lg font-semibold text-lg"
          >
            Sign Out
          </button>
        </div>
      </header>
    </div>
  );
};

export default Profile;
