import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PieChart,
  Pie,
  Tooltip,
  Cell,
  ResponsiveContainer,
  Legend,
} from "recharts";
import Header from "./Header";

const All = () => {
  const defaultUrl = "https://www.youtube.com/watch?v=c1GMcr2x4dI";
  const [videoUrl, setVideoUrl] = useState("");
  const [commentData, setCommentData] = useState(null);
  const [pieChartData, setPieChartData] = useState([]);

  const [borderColor, setBorderColor] = useState("border-gray-400");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          "https://sentiment-cyan.vercel.app/analyze",
          {
            url: defaultUrl,
          }
        );
        // Transform the data for the Pie Chart
        const dataForPieChart = [
          { name: "Positive Comments", value: response.data.positive_comments },
          { name: "Negative Comments", value: response.data.negative_comments },
        ];

        setPieChartData(dataForPieChart);
        setCommentData(response.data);
      } catch (error) {
        console.error("Error fetching default data:", error);
      }
    };

    fetchData(); // Fetch data for the default URL
  }, []);

  const handleSubmit = async () => {
    if (!videoUrl) {
      alert("Please enter a YouTube URL.");
      return;
    }

    try {
      const response = await axios.post(
        "https://sentiment-cyan.vercel.app/analyze",
        {
          url: videoUrl,
        }
      );

      // Transform the data for the Pie Chart
      const dataForPieChart = [
        { name: "Positive Comments", value: response.data.positive_comments },
        { name: "Negative Comments", value: response.data.negative_comments },
      ];

      setPieChartData(dataForPieChart);
      setCommentData(response.data);
    } catch (error) {
      console.error("Error sending URL to backend:", error);
    }
  };

  const handleFocus = () => {
    setBorderColor("border-red-700");
  };

  const handleBlur = () => {
    setBorderColor("border-gray-400");
  };

  const COLORS = ["#0088FE", "#FFBB28"];

  return (
    <div>
      <Header />

      {/* Search Component */}
      <div className="flex justify-center mt-8">
        <div
          className={`flex items-center bg-white rounded-full border ${borderColor} p-1`}
        >
          <input
            style={{ width: "35rem" }}
            placeholder="YouTube Link here..."
            className="pl-5 h-10 text-base text-gray-900 rounded-full focus:ring-blue-500 focus:border-blue-500 outline-none placeholder:text-base"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            onFocus={handleFocus}
            onBlur={handleBlur}
          />
          <button
            type="submit"
            className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-full text-base px-4 h-10"
            onClick={handleSubmit}
          >
            Search
          </button>
        </div>
      </div>

      {/* Main Component */}
      <div
        className="flex justify-between items-center mt-10"
        style={{ paddingLeft: 80, paddingRight: 80 }}
      >
        <div>
          {pieChartData.length > 0 && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie
                  data={pieChartData}
                  cx="50%"
                  cy="50%"
                  outerRadius={150}
                  fill="#8884d8"
                  dataKey="value"
                  label
                >
                  {pieChartData.map((entry, index) => (
                    <Cell
                      key={`cell-${index}`}
                      fill={COLORS[index % COLORS.length]}
                    />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>
        <div class="dark:bg-gray-700 bg-gray-200">
          <div class="max-w-sm mx-auto bg-white dark:bg-gray-900 rounded-lg overflow-hidden shadow-lg">
            <div class="border-b px-4 pb-6">
              <div class="text-center">
                <img
                  class="h-32 w-32 rounded-full border-4 border-white dark:border-gray-800 mx-auto my-2"
                  src={commentData.profile_picture_url}
                  alt=""
                />

                <div class="py-2">
                  <div className="flex justify-center items-center gap-1">
                    <h3 class="font-bold text-2xl text-gray-800 dark:text-white mb-1">
                      Channel Name
                    </h3>
                  </div>
                  <div class="inline-flex text-gray-700 dark:text-gray-300 items-center">
                    <p className="text-md">Number of Subscriber</p>
                  </div>
                </div>
              </div>
              <div class="flex-col">
                <button className="mb-2 w-full rounded-full border-2 border-gray-400 dark:border-gray-700 font-semibold text-black dark:text-white px-4 py-2">
                  Total Comments: {commentData.total_comments}
                </button>

                <button className="mb-2 w-full rounded-full border-2 border-gray-400 dark:border-gray-700 font-semibold dark:text-white px-4 py-2 text-green-700 font-sans">
                  Positive Comments: {commentData.positive_comments}
                </button>

                <button className="mb-2 w-full rounded-full border-2 border-gray-400 dark:border-gray-700 font-semibold text-red-800 dark:text-white px-4 py-2 font-sans">
                  Negative Comments:
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default All;
