import React, { useState } from "react";
import axios from "axios";

const Senti = () => {
  const [url, setUrl] = useState("");
  const [response, setResponse] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const res = await axios.post("http://127.0.0.1:5000/api/get-comments", {
        url,
      });
      setResponse(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="flex justify-center items-center mt-10">
      <input
        type="text"
        placeholder="Enter Youtube Link"
        className="p-3 ml-3 mr-2 bg-gray-100 text-black rounded-full outline-none"
        value={url}
        onChange={(event) => setUrl(event.target.value)}
      />
      <button
        className="bg-blue-700 p-3 text-white rounded-full"
        onClick={handleSubmit}
      >
        Submit
      </button>
      {response && (
        <div>
          <p>Total comments: {response.total_comments}</p>
          <p>Positive comments: {response.positive_comments}</p>
          <p>Negative comments: {response.negative_comments}</p>
        </div>
      )}
    </div>
  );
};

export default Senti;
